    public String drawMeListLimit(int iJSPCommand, int vectsize, int start, int maxtoget, String first, String prev,
    String next, String last) {
        String str = "";
        if (vectsize > 0) {
            if (vectsize > maxtoget) {
                
                if( iJSPCommand==JSPCommand.LIST){
                    if(start==0)
                        iJSPCommand=JSPCommand.FIRST;
                    else{
                        if((start + maxtoget)>=vectsize)
                            iJSPCommand=JSPCommand.LAST;
                    }
                }
                
                
                str = "<table width=\"100%\"><tr><td align=\"right\">";
                switch (iJSPCommand) {
                    case JSPCommand.LIST:
                        str = str + "List : " + (start + 1) + " - " + (start + maxtoget) + ", Total : " + vectsize;
                        str = str + "</td></tr><tr><td align=\"right\">";
                        str = str + "<a href=\"javascript:" + first + "()\">"+listFirstCaption+"</a> | <a href=\"javascript:" + prev +
                        "()\">"+listPrevCaption+"</a> | <a href=\"javascript:" + next +
                        "()\">"+listNextCaption+"</a> | <a href=\"javascript:" + last + "()\">"+listLastCaption+"</a>";
                        break;
                        
                    case JSPCommand.FIRST:
                        str = str + "List : " + (start + 1) + " - " + (start + maxtoget) + ", Total : " + vectsize;
                        str = str + "</td></tr><tr><td align=\"right\">";
                        str = str + listFirstCaption+ " | "+listPrevCaption+" | <a href=\"javascript:" + next +
                        "()\">"+listNextCaption+"</a> | <a href=\"javascript:" + last + "()\">"+listLastCaption+"</a>";
                        break;
                    case JSPCommand.LAST:
                        if ((vectsize % maxtoget) > 0) {
                            start = vectsize - (vectsize % maxtoget);
                        }
                        str = str + "List : " + (start + 1) + " - " + (vectsize) + ", Total : " + vectsize;
                        str = str + "</td></tr><tr><td align=\"right\">";
                        str = str + "<a href=\"javascript:" + first + "()\">"+listFirstCaption+"</a> | <a href=\"javascript:" + prev +
                        "()\">"+listPrevCaption+"</a> | "+listNextCaption+" | "+listLastCaption;
                        break;
                    case JSPCommand.PREV:
                        if ((start - maxtoget) < 0) {
                            str = str + "List : " + 1 + " - " + maxtoget + ", Total : " + vectsize;
                            str = str + "</td></tr><tr><td align=\"right\">";
                            str = str + listFirstCaption+" | "+listPrevCaption+" | <a href=\"javascript:" + next +
                            "()\">"+listNextCaption+"</a> | <a href=\"javascript:" + last + "()\">"+listLastCaption+"</a>";
                        }
                        else {
                            //start = start-maxtoget;
                            str = str + "List : " + (start + 1) + " - " + (start + maxtoget) + ", Total : " + vectsize;
                            str = str + "</td></tr><tr><td align=\"right\">";
                            str = str + "<a href=\"javascript:" + first + "()\">"+listFirstCaption+"</a> | <a href=\"javascript:" + prev +
                            "()\">"+listPrevCaption+"</a> | <a href=\"javascript:" + next +
                            "()\">"+listNextCaption+"</a> | <a href=\"javascript:" + last + "()\">"+listLastCaption+"</a>";
                        }
                        break;
                    case JSPCommand.NEXT:
                        if ((start + maxtoget) >= vectsize) {
                            if ((vectsize % maxtoget) > 0) {
                                start = vectsize - (vectsize % maxtoget);
                            }
                            str = str + "List : " + (start + 1) + " - " + (vectsize) + ", Total : " + vectsize;
                            str = str + "</td></tr><tr><td align=\"right\">";
                            str = str + "<a href=\"javascript:" + first + "()\">"+listFirstCaption+"</a> | <a href=\"javascript:" + prev +
                            "()\">"+listPrevCaption+"</a> | "+listNextCaption+" | "+listLastCaption;
                        }
                        else {
                            str = str + "List : " + (start + 1) + " - " + (start + maxtoget) + ", Total : " + vectsize;
                            str = str + "</td></tr><tr><td align=\"right\">";
                            str = str + "<a href=\"javascript:" + first + "()\">"+listFirstCaption+"</a> | <a href=\"javascript:" + prev +
                            "()\">"+listPrevCaption+"</a> | <a href=\"javascript:" + next +
                            "()\">"+listNextCaption+"</a> | <a href=\"javascript:" + last + "()\">"+listLastCaption+"</a>";
                        }
                        break;
                }
                str = str + "</td></tr></table>";
                return str;
            }
            else {
                str = "<table width=\"100%\"><tr><td align=\"right\">List : " + (start + 1) + "-" + vectsize +
                ", Total : " + vectsize;
                str = str + "</td></tr></table>";
                return str;
            }
        }
        return "";
    }
    
